sql.where:
AND bn.USE_AT = 'Y'

	AND bn.SITE_ID = #{searchSiteId}




			AND bn.BANNER_NM LIKE CONCAT('%', #{searchKeyword}, '%')




	AND bn.BANNER_SE_CODE = #{searchSeCode}


