mapper.update:
UPDATE DTN_BANNER
SET
	SITE_ID = #{siteId}
	,BANNER_SE_CODE = #{bannerSeCode}
	,SORT_NO = #{sortNo}
	,PRTNR_ID = #{prtnrId}
	,BANNER_NM = #{bannerNm}
	,BANNER_PATH = #{bannerPath}
	,BANNER_M_PATH = #{bannerMPath}
	,BANNER_LINK = #{bannerLink}
	,BANNER_WINDOW_AT = IFNULL(#{bannerWindowAt}, 'N')
	,BANNER_BGNDE = #{bannerBgnde}
	,BANNER_ENDDE = #{bannerEndde}
	,BANNER_TY_CODE = #{bannerTyCode}
	,BANNER_LBL = #{bannerLbl}
	,BANNER_LBL_CLOR = #{bannerLblClor}
	,DFK = #{dfk}
	,GOODS_ID = #{goodsId}
	,EVT_TXT = #{evtTxt}
	,BCRN_CLOR = #{bcrnClor}
	,FONT_CLOR = #{fontClor}
	,ACTVTY_AT = #{actvtyAt}
	,LAST_UPDT_PNTTM = SYSDATE()
	,LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE	BANNER_ID = #{bannerId}

mapper.delete:
UPDATE DTN_BANNER
SET
	USE_AT = 'N'
	,LAST_UPDT_PNTTM = SYSDATE()
	,LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE	BANNER_ID = #{bannerId}
