mapper.update:
UPDATE DTN_BBS
SET
	BBS_ID	= #{bbsId}
	,CTGRY_ID	= #{ctgryId}
	,NTT_SJ	= #{nttSj}
	,NTT_CN	= #{nttCn}
	,NOTICE_AT	= #{noticeAt}
	,SECRET_AT	= #{secretAt}
	,NTCE_BGNDE	= #{ntceBgnde}
	,NTCE_ENDDE	= #{ntceEndde}
	,NTCR_ID	= #{ntcrId}
	,NTCR_NM	= #{ntcrNm}
	,PASSWORD	= #{password}
	,ATCH_FILE_ID	= #{atchFileId}
	,THUMB_ATCH_FILE_ID	= #{thumbAtchFileId}
	,LAST_UPDT_PNTTM	= SYSDATE()
	,LAST_UPDUSR_ID	= #{lastUpdusrId}
WHERE	NTT_ID = #{nttId}

mapper.delete:
UPDATE DTN_BBS
SET
	LAST_UPDT_PNTTM	= SYSDATE()
	,LAST_UPDUSR_ID	= #{lastUpdusrId}
	,USE_AT = 'N'
WHERE	NTT_ID = #{nttId}

mapper.select:
SELECT	NTT_NO
FROM	DTN_BBS
WHERE	BBS_ID = #{bbsId}
	AND NTT_ID = #{parntscttNo}

mapper.update:
UPDATE	DTN_BBS SET
	NTT_NO = NTT_NO + 1
WHERE
	BBS_ID = #{bbsId}
	AND SORT_ORDR = #{sortOrdr}
	AND NTT_NO > #{nttNo}

UPDATE	DTN_BBS SET
	NTT_NO = #{nttNo}
WHERE
	BBS_ID = #{bbsId}
	AND NTT_ID = #{nttId}
