SET
	CODE_NM	= #{codeNm}
	,CODE_DC	= #{codeDc}
	,CODE_SN	= #{codeSn}
	,LAST_UPDT_PNTTM = SYSDATE()
	,LAST_UPDUSR_ID	= #{lastUpdusrId}
WHERE	CODE_ID = #{codeId}
	AND CODE = #{code}

mapper.delete:
UPDATE DTC_CMMN_DETAIL_CODE
SET
	USE_AT = 'N'
	,LAST_UPDT_PNTTM = SYSDATE()
	,LAST_UPDUSR_ID	= #{lastUpdusrId}
WHERE	CODE_ID = #{codeId}
	AND CODE = #{code}
