mapper.insert:

	SELECT 	IFNULL(MAX(COMMENT_SORT_ORDR),0)+1 AS COMMENT_SORT_ORDR
	FROM 	DTN_COMMENT
	WHERE 	CNTNTS_ID= #{cntntsId} 
			AND CNTNTS_SE_CODE = #{cntntsSeCode}

