mapper.update:
UPDATE DTN_COMMENT
SET
	COMMENT_CN = #{commentCn}
	,LAST_UPDT_PNTTM = SYSDATE()
	,LAST_UPDUSER_ID = #{lastUpduserId}
	,SCORE = #{score}
	,SECRET_AT = #{secretAt}
	,WRTER_ID = #{wrterId}
	,ATCH_FILE_ID = #{atchFileId}
WHERE	COMMENT_ID = #{commentId}

mapper.delete:
UPDATE 	DTN_COMMENT
SET
	USE_AT = 'N'
	,LAST_UPDT_PNTTM = SYSDATE()
	,LAST_UPDUSER_ID = #{lastUpduserId}
WHERE	COMMENT_ID = #{commentId}

mapper.update:
UPDATE DTN_COMMENT
SET
	LAST_UPDT_PNTTM = SYSDATE()
	,LAST_UPDUSER_ID = #{lastUpduserId}
	,ATCH_FILE_ID = NULL
WHERE COMMENT_ID = #{commentId}

mapper.select:
SELECT COUNT(*) CNT
FROM	DTN_COMMENT
WHERE	CNTNTS_ID = #{cntntsId}
	AND CNTNTS_SE_CODE = #{cntntsSeCode}
	AND	WRTER_ID = #{wrterId}
	AND USE_AT = 'Y'
