mapper.insert:
INSERT INTO DTN_MBER (
	ESNTL_ID
	,SITE_ID
	,MBER_ID
	,PASSWORD
	,MBER_NM
	,EMAIL
	,SEXDSTN
	,AGRDE
	,MBER_STTUS
	,MBER_TY_CODE
	,CLIENT_CD
	,USER_KEY
	,POINT_YN
	,EZMIL_YN
	,SPECIAL_USE_YN
	,RECEIPT_YN
	,SBSCRB_DE
	,LOCK_AT
	,LOCK_CNT
	,LOCK_LAST_PNTTM
	,GROUP_ID
	,AUTHOR_CODE
	,BRTHDY
	,MOBLPHON
) VALUES (
	#{esntlId}
	,#{siteId}
	,#{mberId}
	,#{password}
	,#{mberNm}
	,#{email}
	,#{sexdstn}
	,IF(#{agrde} = '', NULL, #{agrde})
	,#{mberSttus}
	,#{mberTyCode}
	,#{clientCd}
	,#{userKey}
	,#{pointYn}
	,#{ezmilYn}
	,#{specialUseYn}
	,#{receiptYn}
	,SYSDATE()
	,NULL
	,NULL
	,NULL
	,#{groupId}
	,#{authorCode}
	,#{birthday}
	,#{moblphon}
)

mapper.update:
UPDATE DTN_MBER
SET

	SITE_ID = IFNULL(NULLIF(#{siteId},''), 'SITE_00000')

		,MBER_NM = #{mberNm}


		,EMAIL = #{email}


		,MBER_STTUS = #{mberSttus}


		,MBER_TY_CODE = #{mberTyCode}


		,AUTHOR_CODE = #{authorCode}


		,SEXDSTN = #{sexdstn}


		,AGRDE = #{agrde}


		,ADULT_CRT_YN = #{adultCrtYn}


		,SBSCRB_MBER_AT = IFNULL(#{sbscrbMberAt},'N')


		,BRTHDY = #{birthday}


		,MOBLPHON = #{moblphon}


WHERE	ESNTL_ID = #{esntlId}

UPDATE DTN_MBER
SET	
	MBER_STTUS = #{mberSttus}
WHERE	ESNTL_ID = #{esntlId}

UPDATE	DTN_MBER
SET
	PASSWORD = #{password}
WHERE	MBER_ID = #{mberId}

mapper.select:
SELECT
	COUNT(*) AS CNT
FROM	DTN_MBER
WHERE	CLIENT_CD = #{clientCd}
	AND USER_KEY = #{userKey}

mapper.update:
UPDATE DTN_MBER
SET
	POINT_YN = #{pointYn}
	,EZMIL_YN = #{ezmilYn}
	,SPECIAL_USE_YN = #{specialUseYn}
	,RECEIPT_YN = #{receiptYn}
WHERE	CLIENT_CD = #{clientCd}
	AND USER_KEY = #{userKey}
