mapper.select:
SELECT qa.QA_ID ,qa.SITE_ID ,qa.GOODS_ID ,g.GOODS_NM ,qa.ATCH_FILE_ID ,qa.QESTN_SJ ,qa.QESTN_CN ,qa.WRITNG_DE ,qa.WRTER_ID ,qa.WRTER_NM ,qa.EMAIL_ADRES ,qa.WRTER_TELNO ,qa.WRITNG_PASSWORD ,qa.ANSWER_CN ,qa.ANSWER_DE ,qa.ANSWER_ID ,qa.ANSWER_NM ,qa.QNA_PROCESS_STTUS_CODE ,qa.QA_SE_CODE ,qa.QESTN_TY_CODE ,cd.CODE_NM AS QESTN_TY_NM ,qa.SECRET_AT ,qa.FRST_REGIST_PNTTM ,qa.FRST_REGISTER_ID ,qa.LAST_UPDT_PNTTM ,qa.LAST_UPDUSR_ID ,qa.USE_AT FROM DTN_QAINFO qa LEFT JOIN STN_GOODS g ON g.GOODS_ID = qa.GOODS_ID LEFT JOIN STN_PRTNR_CMPNY_MAPNG pcm ON pcm.PCMAPNG_ID = g.PCMAPNG_ID AND pcm.USE_AT = 'Y' LEFT JOIN STN_CMPNY c ON c.CMPNY_ID = pcm.CMPNY_ID AND c.USE_AT='Y' LEFT JOIN DTC_CMMN_DETAIL_CODE cd ON cd.CODE = qa.QESTN_TY_CODE AND cd.CODE_ID IN ('CMS018','CMS019')
ORDER BY QA_ID DESC
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

sql.where:
AND qa.USE_AT = 'Y'

	AND qa.SITE_ID = #{searchSiteId}


	AND qa.QNA_PROCESS_STTUS_CODE = #{qnaProcessSttusCode}


	AND qa.WRTER_ID = #{wrterId}


	AND qa.FRST_REGISTER_ID = #{frstRegisterId}


	AND qa.GOODS_ID = #{goodsId}


	AND qa.QA_SE_CODE = #{qaSeCode}




			AND qa.QESTN_SJ LIKE CONCAT('%', #{searchKeyword}, '%')


			AND qa.QESTN_CN LIKE CONCAT('%', #{searchKeyword}, '%')


			AND qa.ANSWER_CN LIKE CONCAT('%', #{searchKeyword}, '%')


			AND ( 	qa.QESTN_CN LIKE CONCAT('%', #{searchKeyword}, '%')
				OR 	qa.ANSWER_CN LIKE CONCAT('%', #{searchKeyword}, '%')
				)


			AND qa.FRST_REGIST_PNTTM BETWEEN DATE_ADD(NOW(),INTERVAL -1 WEEK ) AND NOW()


			AND qa.FRST_REGIST_PNTTM BETWEEN DATE_ADD(NOW(),INTERVAL -1 MONTH ) AND NOW()


			AND qa.FRST_REGIST_PNTTM BETWEEN DATE_ADD(NOW(),INTERVAL -3 MONTH ) AND NOW()




	AND g.GOODS_NM LIKE CONCAT('%', #{searchGoodsNm}, '%')


	AND qa.QESTN_TY_CODE = #{searchQestnTyCode}


	AND qa.QNA_PROCESS_STTUS_CODE = #{searchQnaProcessSttusCode}


	AND c.CMPNY_ID = #{searchCmpnyId}


	AND c.CMPNY_NM LIKE CONCAT('%', #{searchCmpnyNm}, '%')


	 AND qa.WRITNG_DE >= RPAD(REPLACE(#{searchBgnde}, '-', ''), 14, '0') 


	 AND qa.WRITNG_DE < DATE_FORMAT(DATE_ADD(STR_TO_DATE(RPAD(REPLACE(#{searchEndde}, '-', ''), 14, '0'),'%Y%m%d%H%i%s'), INTERVAL 1 DAY), '%Y%m%d%H%i%s')

mapper.update:
UPDATE DTN_QAINFO
SET 
	SITE_ID = #{siteId}
	,QESTN_SJ = #{qestnSj}
	,QESTN_CN = #{qestnCn}
	,WRITNG_DE = #{writngDe}
	,WRTER_ID = #{wrterId}
	,WRTER_NM = #{wrterNm}
	,EMAIL_ADRES = #{emailAdres}
	,WRTER_TELNO = #{wrterTelno}
	,WRITNG_PASSWORD = #{writngPassword}
	,ANSWER_CN = #{answerCn}
	,ANSWER_DE = #{answerDe}
	,ANSWER_ID = #{answerId}
	,ANSWER_NM = #{answerNm}
	,QNA_PROCESS_STTUS_CODE = #{qnaProcessSttusCode}
	,LAST_UPDT_PNTTM = SYSDATE() 
	,LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE	QA_ID = #{qaId}

UPDATE DTN_QAINFO
SET 
	QESTN_SJ = #{qestnSj}
	,QESTN_CN = #{qestnCn}
	,WRTER_ID = #{wrterId}
	,WRTER_NM = #{wrterNm}
	,ATCH_FILE_ID = #{atchFileId}
	,EMAIL_ADRES = #{emailAdres}
	,WRTER_TELNO = #{wrterTelno}
	,SECRET_AT=#{secretAt}
	,QESTN_TY_CODE=#{qestnTyCode}
	,LAST_UPDT_PNTTM = SYSDATE()
	,LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE	QA_ID = #{qaId}

UPDATE DTN_QAINFO
SET 
	ANSWER_CN = #{answerCn}
	,ANSWER_DE = DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s')
	,ANSWER_ID = #{answerId}
	,ANSWER_NM = #{answerNm}
	,QNA_PROCESS_STTUS_CODE = #{qnaProcessSttusCode}
	,LAST_UPDT_PNTTM = SYSDATE()
	,LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE	QA_ID = #{qaId}

UPDATE DTN_QAINFO
SET 
	QNA_PROCESS_STTUS_CODE = #{qnaProcessSttusCode}
	,LAST_UPDT_PNTTM = SYSDATE 
	,LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE	QA_ID = #{qaId}

mapper.delete:
UPDATE DTN_QAINFO
SET
	USE_AT = 'N'
	,LAST_UPDT_PNTTM = SYSDATE()
	,LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE	QA_ID = #{qaId}
