mapper.update:
UPDATE STN_GOODS_CTGRY
SET
	UPPER_GOODS_CTGRY_ID = #{upperGoodsCtgryId}
	,GOODS_CTGRY_NM = #{goodsCtgryNm}
	,GOODS_CTGRY_SN = #{goodsCtgrySn}
	,CTGRY_EXPSR_SE_CODE = #{ctgryExpsrSeCode}
	,ACTVTY_AT = #{actvtyAt} 
	,BASS_SORT_CODE = #{bassSortCode}
	,BASS_EXHBI_CODE = #{bassExhbiCode}
	,CTGRY_IMAGE_PATH = #{ctgryImagePath}
WHERE	GOODS_CTGRY_ID = #{goodsCtgryId}

mapper.select:
SELECT GOODS_CTGRY_ID ,GOODS_CTGRY_NM FROM ( SELECT CONCAT(IFNULL(G1,''), IFNULL(G2,''), IFNULL(G3,''), IFNULL(G4,'')) GOODS_CTGRY_ID ,CONCAT(IFNULL(N1,''), IFNULL(N2,''), IFNULL(N3,''), IFNULL(N4,'')) GOODS_CTGRY_NM FROM ( SELECT CASE NUM WHEN '1' THEN a.GOODS_CTGRY_ID ELSE NULL END G1 ,CASE NUM WHEN '2' THEN b.GOODS_CTGRY_ID ELSE NULL END G2 ,CASE NUM WHEN '3' THEN c.GOODS_CTGRY_ID ELSE NULL END G3 ,CASE NUM WHEN '4' THEN d.GOODS_CTGRY_ID ELSE NULL END G4 ,CASE NUM WHEN '1' THEN a.GOODS_CTGRY_NM ELSE NULL END N1 ,CASE NUM WHEN '2' THEN b.GOODS_CTGRY_NM ELSE NULL END N2 ,CASE NUM WHEN '3' THEN c.GOODS_CTGRY_NM ELSE NULL END N3 ,CASE NUM WHEN '4' THEN d.GOODS_CTGRY_NM ELSE NULL END N4 FROM STN_GOODS_CTGRY a LEFT JOIN STN_GOODS_CTGRY b ON b.GOODS_CTGRY_ID = a.UPPER_GOODS_CTGRY_ID AND b.USE_AT = 'Y' LEFT JOIN STN_GOODS_CTGRY c ON c.GOODS_CTGRY_ID = b.UPPER_GOODS_CTGRY_ID AND c.USE_AT = 'Y' LEFT JOIN STN_GOODS_CTGRY d ON d.GOODS_CTGRY_ID = c.UPPER_GOODS_CTGRY_ID AND d.USE_AT = 'Y' JOIN ( SELECT '4' AS NUM FROM DUAL UNION ALL SELECT '3' AS NUM FROM DUAL UNION ALL SELECT '2' AS NUM FROM DUAL UNION ALL SELECT '1' AS NUM FROM DUAL ) u WHERE a.GOODS_CTGRY_ID =
#{goodsCtgryId}
#{searchSubCtgryId}
#{searchThreeCtgryId}
AND a.USE_AT = 'Y' AND a.ACTVTY_AT = 'Y' ORDER BY NUM DESC ) t1 ) t2 WHERE t2.GOODS_CTGRY_ID <> ''
