mapper.select:
SELECT * FROM (SELECT a.ORDER_DLVY_NO ,a.ORDER_NO ,a.ORDER_SETLE_NO ,a.ORDER_KND_CODE ,b.SBSCRPT_CYCLE_SE_CODE ,CASE b.ORDER_KND_CODE WHEN 'SBS' THEN CONCAT(a.SBSCRPT_WEEK_CYCLE,'주') ELSE '' END AS SBSCRPT_WEEK_CYCLE ,b.SBSCRPT_DLVY_WD ,cw.CODE_NM AS SBSCRPT_DLVY_WD_NM ,CASE b.ORDER_KND_CODE WHEN 'SBS' THEN CONCAT(b.SBSCRPT_MT_CYCLE,'개월') ELSE '' END AS SBSCRPT_MT_CYCLE ,CASE b.ORDER_KND_CODE WHEN 'SBS' THEN CONCAT(b.SBSCRPT_DLVY_DAY,'일') ELSE '' END AS SBSCRPT_DLVY_DAY ,b.ORDRR_ID ,b.ORDER_GROUP_NO ,b.ORDRR_NM ,b.CHLDRN_NM ,b.ORDER_STTUS_CODE ,b.NEXT_SETLEDE AS EXCEL_NEXT_SETLEDE ,STR_TO_DATE(CONCAT(b.NEXT_SETLEDE,"00:00:00"),"%Y%m%d") AS NEXT_SETLEDE ,a.ORDER_ODR ,a.ORDER_CO ,a.SLE_AMOUNT ,a.DLVY_AMOUNT ,a.DLVY_USER_NM ,a.DSCNT_AMOUNT ,a.TELNO ,a.DLVY_ZIP ,a.DLVY_ADRES ,a.DLVY_ADRES_DETAIL ,a.DLVY_MSSAGE ,a.DLVY_STTUS_CODE ,cd.CODE_NM AS DLVY_STTUS_CODE_NM ,a.HDRY_ID ,a.HDRY_DLVY_DE ,IFNULL(a.INVC_NO, '') AS INVC_NO ,a.ORDER_REQ_STTUS_CODE ,a.REQ_TY_CODE ,ce.CODE_NM AS REQ_TY_CODE_NM ,a.REGIST_PNTTM ,b.ORDER_PNTTM ,b.GOODS_AMOUNT ,a.LAST_UPDT_PNTTM ,a.LAST_UPDUSR_ID ,DATE_FORMAT(s.SETLE_PNTTM ,'%Y-%m-%d %H:%i:%S') AS SETLE_PNTTM
,s.SETLE_STTUS_CODE ,s.SETLE_TOT_AMOUNT , IFNULL(s.SETLE_POINT, 0) AS SETLE_POINT , IFNULL(s.SETLE_CARD_AMOUNT, 0) AS SETLE_CARD_AMOUNT ,cs.CODE_NM AS SETLE_STTUS_CODE_NM ,g.GOODS_ID ,g.GOODS_NM ,g.GOODS_PC ,g.MODEL_NM ,g.VCH_CODE ,g.VCH_VALID_PD ,g.GOODS_KND_CODE ,c.CMPNY_NM ,CONCAT( IF(ISNULL(sgc3.GOODS_CTGRY_NM) or sgc3.GOODS_CTGRY_NM = 'ROOT','',sgc3.GOODS_CTGRY_NM), IF(ISNULL(sgc3.GOODS_CTGRY_NM) or sgc3.GOODS_CTGRY_NM = 'ROOT','','>') ,IF(ISNULL(sgc2.GOODS_CTGRY_NM) or sgc2.GOODS_CTGRY_NM = 'ROOT','',sgc2.GOODS_CTGRY_NM),IF(ISNULL(sgc2.GOODS_CTGRY_NM) or sgc2.GOODS_CTGRY_NM = 'ROOT','','>') ,IF(ISNULL(sgc.GOODS_CTGRY_NM) or sgc.GOODS_CTGRY_NM = 'ROOT','',sgc.GOODS_CTGRY_NM) ) AS GOODS_CTGRY_NM , CASE WHEN b.EXPRN_USE_AT = 'Y' THEN '체험구독' ELSE co.CODE_NM END AS ORDER_STTUS_CODE_NM
,e.API_ID ,p.PRTNR_ID ,p.PRTNR_NM ,(SELECT GROUP_CONCAT(GITEM_NM SEPARATOR '/') FROM STN_ORDER_ITEM WHERE ORDER_NO = b.ORDER_NO GROUP BY ORDER_NO) AS ORDER_ITEM ,b.EXPRN_USE_AT , e.HDRY_NM
) X ORDER BY X.SETLE_PNTTM DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

sql.where:
((b.ORDER_STTUS_CODE = 'ST02' OR (b.ORDER_STTUS_CODE IN ('ST04','ST99') AND a.ORDER_REQ_STTUS_CODE = 'O')) AND ((a.REQ_TY_CODE IN ('C03', 'E03', 'R03') OR a.REQ_TY_CODE IS NULL)))
AND c.CMPNY_ID = #{searchCmpnyId}
AND a.ORDER_REQ_STTUS_CODE = #{searchOrderReqSttusCode}
AND a.REQ_TY_CODE = #{searchReqTyCode}
AND s.SETLE_STTUS_CODE = #{searchSetleSttusCode}
AND a.DLVY_STTUS_CODE = #{searchDlvySttusCode}
AND b.ORDER_STTUS_CODE = #{searchOrderSttusCode}
AND s.SETLE_PNTTM >= STR_TO_DATE(CONCAT(#{searchBgnde}, ' 00:00:00'), '%Y-%m-%d %H:%i:%s')
AND s.SETLE_PNTTM < STR_TO_DATE(CONCAT(#{searchEndde},' 00:00:00'), '%Y-%m-%d') + INTERVAL 1 DAY
AND b.ORDER_NO LIKE CONCAT( '%' , #{searchOrderNo} , '%' )
AND g.GOODS_NM LIKE CONCAT( '%' , #{searchGoodsNm} , '%' )
AND b.ORDRR_NM LIKE CONCAT( '%' , #{searchOrdrrId} , '%' )
AND g.GOODS_ID LIKE CONCAT( '%' , #{searchGoodsId} , '%' )
AND sgc.GOODS_CTGRY_ID IN ( SELECT #{searchGoodsCtgryId} AS GOODS_CTGRY_ID FROM DUAL UNION ALL SELECT AA.GOODS_CTGRY_ID FROM STN_GOODS_CTGRY AA WHERE AA.UPPER_GOODS_CTGRY_ID = #{searchGoodsCtgryId} UNION ALL SELECT BB.GOODS_CTGRY_ID FROM STN_GOODS_CTGRY AA INNER JOIN STN_GOODS_CTGRY BB ON BB.UPPER_GOODS_CTGRY_ID = AA.GOODS_CTGRY_ID WHERE AA.UPPER_GOODS_CTGRY_ID = #{searchGoodsCtgryId} UNION ALL SELECT CC.GOODS_CTGRY_ID FROM STN_GOODS_CTGRY AA INNER JOIN STN_GOODS_CTGRY BB ON BB.UPPER_GOODS_CTGRY_ID = AA.GOODS_CTGRY_ID INNER JOIN STN_GOODS_CTGRY CC ON CC.UPPER_GOODS_CTGRY_ID = BB.GOODS_CTGRY_ID WHERE AA.UPPER_GOODS_CTGRY_ID = #{searchGoodsCtgryId} UNION ALL SELECT DD.GOODS_CTGRY_ID FROM STN_GOODS_CTGRY AA INNER JOIN STN_GOODS_CTGRY BB ON BB.UPPER_GOODS_CTGRY_ID = AA.GOODS_CTGRY_ID INNER JOIN STN_GOODS_CTGRY CC ON CC.UPPER_GOODS_CTGRY_ID = BB.GOODS_CTGRY_ID INNER JOIN STN_GOODS_CTGRY DD ON DD.UPPER_GOODS_CTGRY_ID = CC.GOODS_CTGRY_ID WHERE AA.UPPER_GOODS_CTGRY_ID = #{searchGoodsCtgryId} )
AND c.CMPNY_NM LIKE CONCAT('%', #{searchCmpnyNm}, '%')
AND b.ORDER_KND_CODE = #{searchOrderKndCode}

mapper.update:
UPDATE STN_ORDER_DLVY d

	LAST_UPDT_PNTTM=SYSDATE()
	,LAST_UPDUSR_ID=#{lastUpdusrId}

			,ORDER_CO=#{orderCo}


			,SBSCRPT_WEEK_CYCLE=#{sbscrptWeekCycle}	


			,SBSCRPT_DLVY_WD=#{sbscrptDlvyWd}


			,SBSCRPT_MT_CYCLE=#{sbscrptMtCycle}		


			,SBSCRPT_DLVY_DAY=#{sbscrptDlvyDay}


			,DLVY_MSSAGE=#{dlvyMssage}


			,DLVY_USER_NM=#{dlvyUserNm}


			,TELNO=#{telno}


			,DLVY_ZIP=#{dlvyZip}


			,DLVY_ADRES=#{dlvyAdres}


			,DLVY_ADRES_DETAIL=#{dlvyAdresDetail}


			,DSCNT_AMOUNT=#{dscntAmount}

		,GOODS_AMOUNT = (SELECT o.TOT_AMOUNT FROM STN_ORDER o WHERE o.ORDER_NO = d.ORDER_NO)
		,DSCNT_AMOUNT = (SELECT -o.DSCNT_AMOUNT FROM STN_ORDER o WHERE o.ORDER_NO = d.ORDER_NO)
		,DLVY_AMOUNT = (SELECT o.DLVY_AMOUNT+o.ISLAND_DLVY_AMOUNT FROM STN_ORDER o WHERE o.ORDER_NO = d.ORDER_NO)
		,SETLE_AMOUNT= 
		 (SELECT o.TOT_AMOUNT+o.DSCNT_AMOUNT FROM STN_ORDER o WHERE o.ORDER_NO = d.ORDER_NO)


			, ORDER_INFO=#{orderInfo}

		, SLE_AMOUNT=(SELECT o.TOT_AMOUNT FROM STN_ORDER o WHERE o.ORDER_NO = d.ORDER_NO)

WHERE d.ORDER_DLVY_NO = #{orderDlvyNo}

UPDATE STN_ORDER_DLVY d
SETLE_AMOUNT= (SELECT o.TOT_AMOUNT+ (o.DSCNT_AMOUNT-(SELECT IFNULL(SUM(GITEM_PC),0) FROM STN_ORDER_ITEM WHERE ORDER_NO = o.ORDER_NO AND GISTEM_SE_CODE = 'F' AND GITEM_PC < 0)) -(SELECT IFNULL(SUM(GITEM_PC),0) FROM STN_ORDER_ITEM WHERE ORDER_NO = o.ORDER_NO AND GISTEM_SE_CODE = 'F' AND GITEM_PC > 0) FROM STN_ORDER o WHERE o.ORDER_NO = d.ORDER_NO) , SLE_AMOUNT= (SELECT o.TOT_AMOUNT+ -(SELECT IFNULL(SUM(GITEM_PC),0) FROM STN_ORDER_ITEM WHERE ORDER_NO = o.ORDER_NO AND GISTEM_SE_CODE = 'F') FROM STN_ORDER o WHERE o.ORDER_NO = d.ORDER_NO) ,OPTN_AMOUNT =(SELECT IFNULL(SUM(GITEM_PC),0) FROM STN_ORDER_ITEM WHERE ORDER_NO = d.ORDER_NO AND GISTEM_SE_CODE IN ('A','D')) ,DSCNT_AMOUNT = -(SELECT o.DSCNT_AMOUNT-(SELECT IFNULL(SUM(GITEM_PC),0) FROM STN_ORDER_ITEM WHERE ORDER_NO = o.ORDER_NO AND GISTEM_SE_CODE = 'F' AND GITEM_PC < 0 ) FROM STN_ORDER o WHERE o.ORDER_NO = d.ORDER_NO)
WHERE d.ORDER_DLVY_NO = (SELECT a.no FROM (SELECT MAX(ORDER_DLVY_NO) as no FROM STN_ORDER_DLVY WHERE ORDER_NO = #{orderNo}) AS a);

UPDATE STN_ORDER_DLVY
SET INVC_NO = #{invcNo}
	, DLVY_STTUS_CODE = #{dlvySttusCode}
	, LAST_UPDT_PNTTM = SYSDATE()
	, LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE ORDER_DLVY_NO = #{orderDlvyNo}

UPDATE STN_ORDER_DLVY
SET 	
	LAST_UPDT_PNTTM = SYSDATE()	
	, LAST_UPDUSR_ID = #{lastUpdusrId}	

	, DLVY_STTUS_CODE = #{dlvySttusCode}	

WHERE ORDER_DLVY_NO = #{orderDlvyNo}

UPDATE STN_ORDER_DLVY
SET REQ_TY_CODE = #{reqTyCode}
	, LAST_UPDT_PNTTM = SYSDATE()
	, LAST_UPDUSR_ID = #{lastUpdusrId}

		, ORDER_REQ_STTUS_CODE = #{orderReqSttusCode}


		, DLVY_STTUS_CODE = #{dlvySttusCode}

	, CANCEL_USR_ID = #{cancelUsrId}
	, CANCEL_PNTTM = SYSDATE()
	, CANCEL_AMOUNT = #{cancelAmount}
WHERE
	1=1

		AND ORDER_NO = #{orderNo}
		AND ORDER_ODR = (SELECT
							MAX(ORDER_ODR)
						FROM STN_ORDER_DLVY A
						JOIN STN_ORDER_SETLE B ON A.ORDER_SETLE_NO = B.ORDER_SETLE_NO AND B.SETLE_STTUS_CODE = 'S'
						WHERE ORDER_NO = #{orderNo})


		AND ORDER_DLVY_NO = #{orderDlvyNo}

mapper.sql:
WHERE B.USE_AT = 'Y'
AND A.ORDER_REQ_STTUS_CODE IN ('E', 'R')
AND B.ORDRR_ID = #{ordrrId}
