mapper.insert:
INSERT INTO STN_GOODS_EVENT
(
	EVENT_NO
	, CMPNY_ID
	, EVENT_SJ
	, EVENT_BEGIN_DT
	, EVENT_END_DT
	, EVENT_CN
	, PRTNR_ID
	, EVENT_THUMBNAIL
	, EVENT_BANNER_IMG
	, EVENT_MAIN_IMG_PC
	, EVENT_MAIN_IMG_MOB
	, EVENT_DETAIL_IMG
	, EVENT_URL
	, EVENT_BRAND_IMG_PC
	, EVENT_BRAND_IMG_MOBILE
	, EVENT_CNT
	, END_AT
	, FRST_REGIST_PNTTM
	, FRST_REGISTER_ID
	, LAST_UPDT_PNTTM
	, LAST_UPDUSR_ID
)
VALUES(
	#{eventNo}
	, #{cmpnyId}
	, #{eventSj}
	, #{eventBeginDt}
	, #{eventEndDt}
	, #{eventCn}
	, #{prtnrId}
	, #{eventThumbnail}
	, #{eventBannerImg}
	, #{eventMainImgPc}
	, #{eventMainImgMob}
	, #{eventDetailImg}
	, #{eventUrl}
	, #{eventBrandImgPc}
	, #{eventBrandImgMobile}
	, #{eventCnt}
	, #{endAt}
	, SYSDATE()
	, #{frstRegisterId}
	, NULL
	, NULL
)

mapper.update:
UPDATE STN_GOODS_EVENT
SET 
	CMPNY_ID = #{cmpnyId}
	, EVENT_SJ = #{eventSj}
	, EVENT_BEGIN_DT = #{eventBeginDt}
	, EVENT_END_DT = #{eventEndDt}
	, EVENT_CN = #{eventCn}
	, PRTNR_ID = #{prtnrId}

	, EVENT_THUMBNAIL = #{eventThumbnail}


	, EVENT_MAIN_IMG_PC = #{eventMainImgPc}


	, EVENT_MAIN_IMG_MOB = #{eventMainImgMob}

	, EVENT_URL = #{eventUrl}
	, EVENT_CNT = #{eventCnt}
	, END_AT = #{endAt}
	, LAST_UPDT_PNTTM = SYSDATE()
	, LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE EVENT_NO = #{eventNo}

mapper.insert:
INSERT INTO STN_GOODS_EVENT_MAPNG(
	EVENT_NO
	, GOODS_ID
	, EVENT_RANGE_SE_CODE
)
VALUES(
	#{eventNo}
	, #{goodsId}
	, #{eventRangeSeCode}
)

mapper.select:
SELECT A.GOODS_NM , A.GOODS_ID , A.MRKT_PC , A.MRKT_USE_AT , A.GOODS_PC , D.BRAND_NM , E.GOODS_IMAGE_THUMB_PATH , (SELECT GOODS_LRGE_IMAGE_PATH FROM STN_GOODS_IMAGE WHERE GOODS_ID = A.GOODS_ID AND GOODS_IMAGE_SE_CODE = 'GNR' ORDER BY GOODS_IMAGE_SN ASC LIMIT 1 ) AS GOODS_LRGE_IMAGE_PATH , A.DLVY_SE_CODE FROM STN_GOODS A JOIN STN_GOODS_EVENT_MAPNG B ON A.GOODS_ID = B.GOODS_ID JOIN STN_GOODS_EVENT C ON B.EVENT_NO = C.EVENT_NO JOIN STN_GOODS_BRAND D ON A.BRAND_ID = D.BRAND_ID JOIN STN_GOODS_IMAGE E ON A.GOODS_ID = E.GOODS_ID WHERE C.EVENT_BEGIN_DT <= SYSDATE() AND C.EVENT_END_DT >= SYSDATE() AND C.END_AT = 'N'
AND C.PRTNR_ID = #{searchPrtnrId}
AND A.REGIST_STTUS_CODE = 'C' GROUP BY A.GOODS_ID ORDER BY RAND()
