SELECT g.GOODS_ID ,g.PCMAPNG_ID ,p.PRTNR_ID ,c.CMPNY_ID ,c.CMPNY_NM ,c.CMPNY_TELNO ,g.GOODS_CTGRY_ID ,g.GOODS_KND_CODE ,(SELECT GOODS_IMAGE_PATH FROM STN_GOODS_IMAGE WHERE GOODS_ID = g.GOODS_ID AND GOODS_IMAGE_SE_CODE = 'GNR' ORDER BY GOODS_IMAGE_SN ASC LIMIT 1 ) AS GOODS_TITLE_IMAGE_PATH ,(SELECT GOODS_IMAGE_THUMB_PATH FROM STN_GOODS_IMAGE WHERE GOODS_ID = g.GOODS_ID AND GOODS_IMAGE_SE_CODE = 'GNR' ORDER BY GOODS_IMAGE_SN ASC LIMIT 1 ) AS GOODS_TITLE_IMAGE_THUMB_PATH ,g.GOODS_NM ,g.MODEL_NM ,g.MAKR ,g.ORGPLCE ,g.BRAND_ID ,b.BRAND_NM ,g.CRTFC_MATTER ,g.CNSLT_TELNO ,g.GOODS_INTRCN ,g.EVNT_WORDS ,g.MNGR_MEMO ,g.MVP_SOURC_CN ,g.GOODS_CN ,g.GOODS_PC ,g.GOODS_CO ,g.GOODS_FEE_RATE ,g.GOODS_SPLPC ,g.GOODS_RSRVMNEY ,g.GOODS_RSRVMNEY_RATE_AT ,g.GOODS_RSRVMNEY_RATE ,g.MRKT_PC ,g.TAXT_SE_CODE ,g.DLVY_POLICY_SE_CODE ,CASE WHEN g.DLVY_POLICY_SE_CODE ='DP01' THEN c.CMPNY_DLVY_POLICY_CN ELSE g.DLVY_POLICY_CN END AS DLVY_POLICY_CN ,g.DLVY_SE_CODE ,g.DLVY_PC ,g.ISLAND_DLVY_PC ,g.JEJU_DLVY_PC ,IFNULL(g.FREE_DLVY_PC,0) AS FREE_DLVY_PC ,IFNULL(g.EXPRN_PC,0) AS EXPRN_PC ,g.EXPRN_USE_AT ,g.COMPNO_DSCNT_USE_AT ,IFNULL(g.COMPNO_DSCNT_PC,0) AS COMPNO_DSCNT_PC ,g.SBSCRPT_CYCLE_SE_CODE ,g.SBSCRPT_WEEK_CYCLE ,g.SBSCRPT_DLVY_WD ,g.SBSCRPT_MIN_USE_WEEK ,g.SBSCRPT_MT_CYCLE ,g.SBSCRPT_MIN_USE_MT ,g.SBSCRPT_DLVY_DAY ,g.SBSCRPT_SETLE_DAY ,g.BUNDLE_DLVY_AT ,g.BUNDLE_DLVY_CO ,g.RDCNT ,g.SLE_CO ,g.OPTN_USE_AT ,g.D_OPTN_USE_AT ,g.A_OPTN_USE_AT ,g.F_OPTN_USE_AT ,g.Q_OPTN_USE_AT ,g.S_OPTN_USE_AT ,g.FRST_OPTN_ESSNTL_AT ,g.SOLD_OUT_AT ,g.ADULT_CRT_AT ,g.BEST_ORDR ,g.MAIN_USE_AT ,g.MAIN_SN ,g.MRKT_USE_AT ,g.CHLDRNNM_USE_AT ,g.DPSTRY_AT ,g.DPSTRY_NO_LIST ,g.VCH_CODE ,CASE WHEN g.VCH_CODE ='BAS00' THEN '베이직' WHEN g.VCH_CODE ='WEK00' THEN '주말' WHEN g.VCH_CODE = 'PRM00' THEN '프리미엄' WHEN g.VCH_CODE = 'ETC' THEN '기타' ELSE '' END AS VCH_CODE_NM ,CASE WHEN g.VCH_PD_TY ='MONTH' THEN '월단위' WHEN g.VCH_PD_TY ='DAY' THEN '일단위' WHEN g.VCH_PD_TY ='ETC' THEN '기타' ELSE '' END AS VCH_PD_TYNM ,g.VCH_VALID_PD ,g.VCH_PD_TY ,g.REGIST_STTUS_CODE ,g.GOODS_EXPSR_CODE ,g.FRST_REGIST_PNTTM ,g.FRST_REGISTER_ID ,g.LAST_UPDT_PNTTM ,g.LAST_UPDUSR_ID ,g.USE_AT ,g.HDRY_ID ,h.HDRY_NM , CASE WHEN g.GOODS_ID IN ( SELECT BB.GOODS_ID FROM STN_GOODS_EVENT AA JOIN STN_GOODS_EVENT_MAPNG BB ON AA.EVENT_NO = BB.EVENT_NO WHERE AA.EVENT_BEGIN_DT <= SYSDATE() AND AA.EVENT_END_DT >= SYSDATE()
AND AA.PRTNR_ID = #{searchPrtnrId}
AND AA.END_AT = 'N' ) THEN 'Y' ELSE 'N' END AS EVENT_AT ,gc.UPPER_GOODS_CTGRY_ID AS GOODS_UPPER_CTGRY_ID ,g.D_OPTN_TYPE FROM STN_GOODS g JOIN STN_PRTNR_CMPNY_MAPNG pcm ON pcm.PCMAPNG_ID = g.PCMAPNG_ID AND pcm.USE_AT = 'Y' JOIN STN_PRTNR p ON p.PRTNR_ID = pcm.PRTNR_ID JOIN STN_CMPNY c ON c.CMPNY_ID = pcm.CMPNY_ID AND c.USE_AT='Y' JOIN STN_GOODS_CTGRY gc ON gc.GOODS_CTGRY_ID = g.GOODS_CTGRY_ID LEFT JOIN STN_GOODS_BRAND b ON b.BRAND_ID = g.BRAND_ID LEFT JOIN STN_HDRY_CMPNY h ON g.HDRY_ID = h.HDRY_ID WHERE g.GOODS_ID = #{goodsId}
AND pcm.PRTNR_ID = #{searchPrtnrId}
AND g.REGIST_STTUS_CODE = 'R'
AND g.REGIST_STTUS_CODE = 'C'
AND g.REGIST_STTUS_CODE = 'E'

