UPDATE STN_GOODS
SET
	USE_AT = 'N'
  ,LAST_UPDT_PNTTM = SYSDATE()
  ,LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE	GOODS_ID = #{goodsId}

mapper.select:
SELECT SUM(T_CNT) TCNT ,SUM(R_CNT) RCNT ,SUM(C_CNT) CCNT ,SUM(E_CNT) ECNT FROM ( SELECT 1 AS T_CNT ,CASE sg.REGIST_STTUS_CODE WHEN 'R' THEN 1 ELSE 0 END R_CNT ,CASE sg.REGIST_STTUS_CODE WHEN 'C' THEN 1 ELSE 0 END C_CNT ,CASE sg.REGIST_STTUS_CODE WHEN 'E' THEN 1 ELSE 0 END E_CNT FROM STN_PRTNR_CMPNY_MAPNG pcm INNER JOIN STN_GOODS sg ON sg.PCMAPNG_ID = pcm.PCMAPNG_ID AND pcm.USE_AT = 'Y' WHERE sg.USE_AT = 'Y'
AND pcm.CMPNY_ID = #{searchCmpnyId}
) t1

SELECT g.GOODS_ID ,p.PRTNR_ID ,c.CMPNY_NM ,g.GOODS_CTGRY_ID ,g.GOODS_KND_CODE ,gi.GOODS_LRGE_IMAGE_PATH ,gi.GOODS_MIDDL_IMAGE_PATH ,gi.GOODS_SMALL_IMAGE_PATH ,g.GOODS_NM ,g.MODEL_NM ,g.MAKR ,g.BRAND_ID ,gb.BRAND_NM ,g.GOODS_INTRCN ,g.EVNT_WORDS ,g.GOODS_PC ,g.MRKT_PC ,g.DLVY_PC ,g.DLVY_SE_CODE ,g.MRKT_USE_AT ,g.SBSCRPT_CYCLE_SE_CODE ,g.SBSCRPT_WEEK_CYCLE ,g.SBSCRPT_DLVY_WD ,g.SBSCRPT_MIN_USE_WEEK ,g.SBSCRPT_MT_CYCLE ,g.SBSCRPT_MIN_USE_MT ,g.SBSCRPT_DLVY_DAY ,g.SBSCRPT_SETLE_DAY ,CASE WHEN g.GOODS_ID IN ( SELECT BB.GOODS_ID FROM STN_GOODS_EVENT AA JOIN STN_GOODS_EVENT_MAPNG BB ON AA.EVENT_NO = BB.EVENT_NO WHERE AA.EVENT_BEGIN_DT <= SYSDATE() AND AA.EVENT_END_DT >= SYSDATE()
AND AA.PRTNR_ID = #{searchPrtnrId}
AND AA.END_AT = 'N' ) THEN 'Y' ELSE 'N' END AS EVENT_AT FROM STN_GOODS g JOIN STN_PRTNR_CMPNY_MAPNG pcm ON pcm.PCMAPNG_ID = g.PCMAPNG_ID AND pcm.USE_AT = 'Y' JOIN STN_PRTNR p ON p.PRTNR_ID = pcm.PRTNR_ID JOIN STN_CMPNY c ON c.CMPNY_ID = pcm.CMPNY_ID AND c.USE_AT='Y' JOIN STN_GOODS_CTGRY sgc ON sgc.GOODS_CTGRY_ID = g.GOODS_CTGRY_ID AND sgc.USE_AT = 'Y' LEFT JOIN STN_GOODS_BRAND gb ON gb.BRAND_ID = g.BRAND_ID LEFT JOIN STN_GOODS_IMAGE gi ON gi.GOODS_ID = g.GOODS_ID AND gi.GOODS_IMAGE_SE_CODE = 'GNR' AND gi.GOODS_IMAGE_NO = (SELECT MIN(GOODS_IMAGE_NO) FROM stn_goods_image WHERE GOODS_ID = g.GOODS_ID AND GOODS_IMAGE_SE_CODE = 'GNR')
ORDER BY g.BEST_ORDR DESC,g.LAST_UPDT_PNTTM DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

