INSERT INTO STN_GOODS_ITEM_INFO (
	 GOODS_ID
	,D_OPTN_USE_AT
	,D_OPTN_TYPE
	,A_OPTN_USE_AT
	,S_OPTN_USE_AT
	,F_OPTN_USE_AT
	,Q_OPTN_USE_AT
) VALUES (
			 #{goodsId}
		 ,#{dOptnUseAt}
		 ,#{dOptnType}
		 ,#{aOptnUseAt}
		 ,#{sOptnUseAt}
		 ,#{fOptnUseAt}
		 ,#{qOptnUseAt}
		 )ON DUPLICATE KEY UPDATE
	D_OPTN_USE_AT  = #{dOptnUseAt}
	,D_OPTN_TYPE   = #{dOptnType}
	,A_OPTN_USE_AT = #{aOptnUseAt}
	,S_OPTN_USE_AT = #{sOptnUseAt}
	,F_OPTN_USE_AT = #{fOptnUseAt}
	,Q_OPTN_USE_AT = #{qOptnUseAt}
