sql.where:
A.USE_AT = 'Y'
AND A.SETLE_STTUS_CODE = #{searchSetleSttusCode}
AND B.DLVY_STTUS_CODE = #{searchDlvySttusCode}
AND DATE_FORMAT(A.SETLE_PNTTM, '%Y-%c-%e') >= #{searchBgnde}
AND DATE_FORMAT(A.SETLE_PNTTM, '%Y-%c-%e') <= #{searchEndde}
AND C.ORDER_NO = #{searchOrderNo}
AND D.GOODS_NM LIKE CONCAT( '%' , #{searchGoodsNm} , '%' )
AND E.GOODS_CTGRY_ID IN ( SELECT #{searchGoodsCtgryId} AS GOODS_CTGRY_ID FROM DUAL UNION ALL SELECT AA.GOODS_CTGRY_ID FROM STN_GOODS_CTGRY AA WHERE AA.UPPER_GOODS_CTGRY_ID = #{searchGoodsCtgryId} UNION ALL SELECT BB.GOODS_CTGRY_ID FROM STN_GOODS_CTGRY AA INNER JOIN STN_GOODS_CTGRY BB ON BB.UPPER_GOODS_CTGRY_ID = AA.GOODS_CTGRY_ID WHERE AA.UPPER_GOODS_CTGRY_ID = #{searchGoodsCtgryId} UNION ALL SELECT CC.GOODS_CTGRY_ID FROM STN_GOODS_CTGRY AA INNER JOIN STN_GOODS_CTGRY BB ON BB.UPPER_GOODS_CTGRY_ID = AA.GOODS_CTGRY_ID INNER JOIN STN_GOODS_CTGRY CC ON CC.UPPER_GOODS_CTGRY_ID = BB.GOODS_CTGRY_ID WHERE AA.UPPER_GOODS_CTGRY_ID = #{searchGoodsCtgryId} UNION ALL SELECT DD.GOODS_CTGRY_ID FROM STN_GOODS_CTGRY AA INNER JOIN STN_GOODS_CTGRY BB ON BB.UPPER_GOODS_CTGRY_ID = AA.GOODS_CTGRY_ID INNER JOIN STN_GOODS_CTGRY CC ON CC.UPPER_GOODS_CTGRY_ID = BB.GOODS_CTGRY_ID INNER JOIN STN_GOODS_CTGRY DD ON DD.UPPER_GOODS_CTGRY_ID = CC.GOODS_CTGRY_ID WHERE AA.UPPER_GOODS_CTGRY_ID = #{searchGoodsCtgryId} )
AND B.ORDER_DLVY_NO = ( SELECT MAX(BB.ORDER_DLVY_NO) FROM STN_ORDER AA JOIN STN_ORDER_DLVY BB ON AA.ORDER_NO = BB.ORDER_NO GROUP BY AA.ORDER_NO )
AND F.CMPNY_ID=#{searchCmpnyId}

mapper.select:
SELECT
	count(A.ORDER_SETLE_NO)as count
FROM STN_ORDER_SETLE A
JOIN STN_ORDER_DLVY B ON A.ORDER_SETLE_NO = B.ORDER_SETLE_NO
JOIN STN_ORDER C ON B.ORDER_NO = C.ORDER_NO
WHERE C.ORDER_GROUP_NO = #{orderGroupNo}
AND A.SETLE_STTUS_CODE = 'S'
AND C.GOODS_ID = #{goodsId}
AND C.ORDER_NO != #{orderNo}
